mapper.select:
select pdb.is_id, pdb.is_code, pdb.is_result, pdb.w_id,ckb.w_name whName,
       pdb.is_type, pdb.is_start_time, pdb.is_end_time, pdb.out_status,
       pdb.in_status, pdb.remark, pdb.is_manager,manager.user_name managerName,
       pdb.create_by,creater.user_name creatorName, pdb.create_time,pdb.update_by,
       pdb.update_time,pdb.is_delete, dp.dept_name creatorDept
    from wh_inventory_sheet pdb
    inner join wh_warehouse ckb on pdb.w_id=ckb.w_id
    inner join sys_user manager on pdb.is_manager=manager.user_id
    inner join sys_user creater on pdb.create_by=creater.user_id
    inner join sys_dept dp on dp.dept_id=creater.dept_id

    and pdb.is_code = #{is_code}
    and pdb.is_result = #{is_result}
    and pdb.is_type = #{is_type}
    and pdb.w_id = #{w_id}
    and pdb.is_id = #{isId}
    and pdb.is_delete=0

select gd.g_name,gd.g_code,gd.spec_code,ws.unit,ws.s_id,gt_name,ws.item_quantity, gd.wr_price,ws.sl_id,ws.remark,sl.sl_name from wh_stock ws inner join wh_goods gd on gd.g_id=ws.g_id inner join wh_goods_type gt on gt.gt_id=gd.gt_id inner join wh_storage_location sl on sl.sl_id=ws.sl_id where ws.w_id=#{w_id}
and gd.g_name like concat('%',#{g_name},'%')
and ws.unit = #{unit}
and ws.is_delete=0

mapper.update:
update wh_inventory_sheet
is_code = #{isCode}, is_result = #{isResult}, w_id = #{wId}, is_type = #{isType}, is_start_time = #{isStartTime}, is_end_time = #{isEndTime}, out_status = #{outStatus}, in_status = #{inStatus}, remark = #{remark}, is_manager = #{isManager}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, is_delete = #{isDelete},
where is_id = #{isId}
