mapper.select:
SELECT pdmxb.is_id, pdmxb.isd_id, pdmxb.count_quantity,pdmxb.profit_loss_quantity, pdmxb.quantity_shipped, pdmxb.received_quantity, pdmxb.is_status,pdmxb.count_amount,pdmxb.s_id, gt.gt_name, sl.sl_id, gdb.g_name, gdb.wr_price, gdb.g_id, gdb.g_code,gdb.spec_code,kcb.unit,kcb.item_quantity,sl.sl_name from wh_is_details pdmxb inner join wh_inventory_sheet wis on wis.is_id = pdmxb.is_id inner join wh_stock kcb on pdmxb.s_id=kcb.s_id inner join wh_goods gdb on kcb.g_id=gdb.g_id inner join wh_goods_type gt on gt.gt_id = gdb.gt_id inner join wh_storage_location sl on sl.sl_id=kcb.sl_id
and pdmxb.isd_id in (
#{item}
)
pdmxb.is_id = #{params.isId}
and (gdb.g_name like concat('%', #{params.orderOrName}, '%') or gdb.g_code like concat('%', #{params.orderOrName}, '%'))
and kcb.unit = #{params.unit}
and pdmxb.received_quantity < pdmxb.profit_loss_quantity
and pdmxb.quantity_shipped < -pdmxb.profit_loss_quantity

mapper.update:
update wh_is_details
set is_delete=1
where is_id = #{isId}

mapper.insert:
insert into wh_inventory_sheet
is_code, is_result, w_id, is_type, is_start_time, is_end_time, remark, is_manager, create_by, create_time, update_by, update_time
#{isCode}, #{isResult}, #{wId}, #{isType}, #{isStartTime}, #{isEndTime}, #{remark}, #{isManager}, #{createBy}, #{createTime}, #{updateBy}, #{updateTime}

mapper.select:
select a.is_id, a.is_code, a.is_result, a.w_id, a.is_type, a.is_start_time, a.is_end_time, a.out_status, a.in_status, a.remark, a.is_manager, a.create_by, a.create_time, a.update_by, a.update_time, a.is_delete, b.isd_id as sub_isd_id, b.is_id as sub_is_id, b.s_id as sub_s_id, b.count_quantity as sub_count_quantity, b.profit_loss_quantity as sub_profit_loss_quantity, b.is_status as sub_is_status, b.count_amount as sub_count_amount, b.received_quantity sub_received_quantity, b.quantity_shipped sub_quantity_shipped, b.remark as sub_remark, b.create_by as sub_create_by, b.create_time as sub_create_time, b.update_by as sub_update_by, b.update_time as sub_update_time, b.is_delete as sub_is_delete from wh_inventory_sheet a left join wh_is_details b on b.is_id = a.is_id where a.is_id = #{isId} and a.is_delete = 0 and b.isd_id in (
#{isdId}
)

mapper.update:
update wh_inventory_sheet
is_code = #{isCode}, is_result = #{isResult}, w_id = #{wId}, is_type = #{isType}, is_start_time = #{isStartTime}, is_end_time = #{isEndTime}, out_status = #{outStatus}, in_status = #{inStatus}, remark = #{remark}, is_manager = #{isManager}, create_by = #{createBy}, create_time = #{createTime}, is_delete = #{isDelete},
where is_id = #{isId}

update wh_is_details
received_quantity = #{receivedQuantity}, quantity_shipped = #{quantityShipped}, update_by = #{updateBy}, update_time = #{updateTime},
where isd_id = #{isId} and is_id = #{isdId}
