mapper.select:
select wt.t_id, wt.td_code, wt.doc_status, wt.t_date, wt.out_w_id, ckb.w_name outName,wt.in_w_id,ckb2.w_name inName, wt.out_status, wt.t_type, wt.remark, wt.in_status, wt.t_manager, manager.user_name managerName, wt.create_by,creater.user_name creatorName, dp.dept_name creatorDept, wt.create_time, wt.update_by, wt.update_time, wt.is_delete, wt.reviewer,revieer.user_name revieerName,wt.reviewer_time,wt.comments from wh_transfer wt inner join wh_warehouse ckb on wt.out_w_id=ckb.w_id inner join wh_warehouse ckb2 on wt.in_w_id=ckb2.w_id inner join sys_user manager on wt.t_manager=manager.user_id inner join sys_user creater on wt.create_by=creater.user_id left join sys_user revieer on wt.reviewer=revieer.user_id inner join sys_dept dp on dp.dept_id=creater.dept_id
and wt.td_code = #{tdCode} and wt.doc_status = #{docStatus}
and wt.t_date BETWEEN #{dates[0]} AND #{dates[1]}
and wt.out_status = #{outStatus} or wt.in_status = #{outStatus} and wt.is_delete=0

SELECT tfd.td_id, tfd.t_id, tfd.stock_id, tfd.t_quantity, tfd.quantity_shipped, tfd.received_quantity, tfd.remark tfd_remark, stock.s_id stock_id, stock.g_id, stock.w_id , stock.sl_id, stock.unit, stock.item_quantity, stock.remark, gt.gt_name, g.g_code, g.g_name, g.spec_code, g.or_price, g.wr_price FROM wh_transfer_details tfd INNER JOIN wh_stock stock on tfd.stock_id = stock.s_id INNER JOIN wh_goods g on g.g_id = stock.g_id INNER JOIN wh_goods_type gt on gt.gt_id = g.gt_id
and tfd.t_id = #{params.tid}
and tfd.td_id in (
#{tdid}
)
and g.g_code like concat('%', #{params.codeOrName}, '%') and g.g_name like concat('%', #{params.codeOrName}, '%')
stock.unit = #{unit}
and tfd.t_quantity > tfd.received_quantity
and tfd.t_quantity > tfd.quantity_shipped

select sl_id slId, sl_name slName
from wh_storage_location
where w_id = #{w_id}

SELECT COALESCE(SUM(item_quantity), 0) AS itemQuantity
FROM wh_stock
WHERE w_id = #{w_id}
  AND g_id = #{g_id};

mapper.update:
UPDATE wh_stock
set number_plans = number_plans - #{quantity}
where w_id = #{w_id}
  and sl_id = #{sl_id}
  and g_id = #{g_id}

update wh_transfer
td_code = #{tdCode}, doc_status = #{docStatus}, t_date = #{date}, out_w_id = #{outWId}, in_w_id = #{inWId}, out_status = #{outStatus}, t_type = #{type}, remark = #{remark}, in_status = #{inStatus}, t_manager = #{manager}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, is_delete = #{deleted},
where t_id = #{tid}

update wh_transfer_details

    quantity_shipped = #{quantityShipped},
    received_quantity = #{receivedQuantity},
    update_by = #{updateBy},
    update_time = #{updateTime}

where t_id = #{tid} and td_id = #{tdId}
