mapper.select:


     and ww.w_code = #{w_code}
     and ww.w_name like concat('%', #{w_name}, '%')
     and ww.status = #{status}
     and sd.dept_name = #{deptName}
     and su3.user_name = #{w_supervisor}
    and ww.is_delete = 0

order by  ww.create_time desc

mapper.update:
update wh_warehouse
w_code = #{wCode}, w_name = #{wName}, w_capacity = #{wCapacity}, status = #{status}, w_address = #{wAddress}, is_default = #{isDefault}, w_supervisor = #{wSupervisor}, remark = #{remark}, sort = #{sort}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where w_id = #{wId}

mapper.sql:
and wsl.sl_name like concat('%',#{sl_name},'%')
 and wsl.status = #{status}
 and su3.user_name = #{position_manager}
and wsl.is_delete = 0
